var grayCode = function(n) {
    const result = [0]; // Initialize the result array with the first integer 0

    // Calculate the total number of elements in the gray code sequence (2^n)
    const total = 1 << n;

    // Iterate from 1 to 'total' to generate the gray code sequence
    for (let i = 1; i < total; i++) {
        // The new element is generated by flipping the rightmost bit of the previous element
        const prev = result[result.length - 1];
        let newNum = prev ^ (i & -i); // Use XOR operation to flip the rightmost bit

        result.push(newNum); // Add the new element to the result array
    }

    return result;
};
